import { Component } from '@angular/core';

interface TodoItem {
  description: string;
  done: boolean;
}

@Component({
  selector: 'app-to-do-list',
  templateUrl: './to-do-list.component.html',
  styleUrls: ['./to-do-list.component.css']
})
export class ToDoListComponent {
  allItems: TodoItem[] = [];
  newDescription: string = '';
  addItems: any;

  addItem(form: any) {
    console.warn(form.value.description);
    if (this.newDescription.trim() !== '') {
      this.allItems.push({ description: this.newDescription, done: false });
      this.newDescription = '';
    }

    if (this.newDescription) {
      this.addItems.push(this.newDescription);
      this.newDescription = '';
    }

    return this.allItems;
  }
}
